import { Component } from '@angular/core';
import { Content } from '../helper-files/content-interface';
import { TypeFilterPipe } from '../type-filter.pipe';



@Component({
  selector: 'app-content-list',
  templateUrl: './content-list.component.html',
  styleUrls: ['./content-list.component.scss']
})


export class ContentListComponent {
  contentList: Content[] = [
    { id: 1, title: 'Content 1', description: 'Description 1', creator: 'Creator 1', imgURL:'', type: 'type1', tags: ['action, drama'] },
    { id: 2, title: 'Content 1', description: 'Description 1', creator: 'Creator 1', imgURL:'', type: 'type1', tags: ['action, drama'] },
    { id: 3, title: 'Content 1', description: 'Description 1', creator: 'Creator 1', imgURL:'', type: 'type1', tags: ['action, drama'] },
    { id: 4, title: 'Content 1', description: 'Description 1', creator: 'Creator 1', imgURL:'', type: 'type1', tags: ['action, drama'] },
    { id: 5, title: 'Content 1', description: 'Description 1', creator: 'Creator 1', imgURL:'', type: 'type1', tags: ['action, drama'] },
    { id: 6, title: 'Content 1', description: 'Description 1', creator: 'Creator 1', imgURL:'', type: 'type1', tags: ['action, drama'] },
    { id: 7, title: 'Content 1', description: 'Description 1', creator: 'Creator 1', imgURL:'', type: 'type1', tags: ['action, drama'] },
  ];


searchTitle: string = '';
foundContent: boolean = false;

constructor() { }

searchContent() {
  this.foundContent = this.contentList.some(content => content.title === this.searchTitle);
}


}
